#include <solvers/gradient_descent_solver.h>GradientDescentSolver::GradientDescentSolver(int maxIterations, double solveResidual, int maxLineSearchIterations)	: Solver(maxIterations, solveResidual, maxLineSearchIterations) {	}GradientDescentSolver::~GradientDescentSolver() {}void GradientDescentSolver::computeSearchDirection(ObjectiveFunction *function, const VectorXd &x, VectorXd& dx) {	VectorXd grad;	grad.resize(x.size());	grad.setZero();	function->addGradientTo(grad, x);	dx = -1 * grad;}